import React, { Component } from "react";

export default class SignUp extends Component {
    
    constructor(props) {
        super(props);
        this.UserType = "";
    }
    
    checkIfStudent(e) {
        e.preventDefault();
        this.UserType = "student";
    }
    checkIfTeacher(e) {
        e.preventDefault();
        this.UserType = "teacher";  
    }

    render() {
        return (
            <form>
                <h3>Sign Up</h3>
                
                <div className = "form-group">
                    <button id="student" type="button" class="btn btn-primary btn-lg" onClick="checkIfStudent()" inde>Student</button>
                    <button id="teacher" type="button" class="btn btn-secondary btn-lg" onClick="checkIfTeacher()">Teacher</button>
                </div>

                <p className="forgot-password text-right">
                    <a href="#">Already registered? Sign in!</a>
                </p>
            </form>
        );
    }

